mapper.select:
select *
from kangbank_deal_detail
where dealDate between #{startDay} and #{endDay} and accountNo=#{accountNo}

select *
from kangbank_deal_detail
where dealDate between #{startDay} and #{endDay} and accountNo=#{accountNo}
and dealType=#{dealType}

select count(*)
from kangbank_deal_detail
where dealDate between #{startDay} and #{endDay} and accountNo=#{accountNo}

select count(*)
from kangbank_deal_detail
where dealDate between #{startDay} and #{endDay} and accountNo=#{accountNo}
and dealType=#{dealType}
